import React, { useEffect, useState } from "react";
import ParcelItem from "../components/parcelitem";
x;
export default function TaskPage({ tasks }) {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");
  const API_URL = "https://my.api.mockaroo.com/orders.json?key=e49e6840";

  useEffect(() => {
    fetch(API_URL)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setData(data);
      });
  }, [setData]);

  console.log(data);

  const handlechange = (e) => {
    setSearch(e.target.value);
  };
  const filteredItem = ParcelItem.filter((item) =>
    item.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="Tracker-App">
      <div className="search">
        <h1 className="text">Package Tracker search</h1>
        <form>
          <input
            type="text"
            placeholder="Search"
            className="input"
            onChange={handlechange}
          />
        </form>
      </div>
      {filteredItem.map((item) => {
        return (
          <ParcelItem
            key={ParcelItem.id}
            name={ParcelItem.name}
            parcels={ParcelItem.parcels}
            address={ParcelItem.address}
            pickuploaction={ParcelItem.pickuploaction}
            orderStatus={ParcelItem.orderStatus}
          />
        );
      })}
    </div>
  );
}
